import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { TextField } from '@material-ui/core'
import styled from 'styled-components'

import theme from '../../styles/theme'
import { increaseScore } from '../../redux/actions/gameActions'
import { useCurrentWordSelector } from '../../redux/reducers/game'

const INPUT_VALUE_DEFAULT = 'Start typing...'

const GameInput = () => {
  const dispatch = useDispatch()
  const currenWord = useCurrentWordSelector()

  const [inputWord, setInputWord] = useState()

  const clearInput = () => {
    setInputWord('')
  }

  const handleChange = (value) => {
    setInputWord(value)

    const isValueSameCurrentWord = currenWord === value

    if (!isValueSameCurrentWord) {
      return
    }

    clearInput()
    dispatch(increaseScore())
  }

  return (
    <GameInputWrapper>
      <TextField
        autoFocus
        type="text"
        variant="outlined"
        placeholder={INPUT_VALUE_DEFAULT}
        onChange={(event) => handleChange(event.target.value)}
        value={inputWord}
        inputProps={{
          min: 0,
          style: {
            textAlign: 'center',
            color: theme.colors.primaryBlue,
            borderColor: theme.colors.primaryBlue,
          },
        }}
      />
    </GameInputWrapper>
  )
}

const GameInputWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin-top: 1rem;
`

export default GameInput
