import {
  useScoreSelector,
  useIsPlayerPlayingSelector,
  useTimeSelector,
} from '../redux/reducers/game'

export const useIsGameProgress = () => {
  const score = useScoreSelector()
  const isPlayerPlaying = useIsPlayerPlayingSelector()
  const time = useTimeSelector()

  if (score === 0 && isPlayerPlaying && time === 0) {
    return false
  }

  return true
}
