  const { score, seconds } = useGameSelector()

  const descreaseTime = () => {}

  return (
    <GameInfoWrapper>
      <div>
        <h3>Time Left: {seconds}</h3>
      </div>
      <div>
        <h3>Score: {score}</h3>
      </div>
    </GameInfoWrapper>
  )
}

const GameInfoWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`

export default GameInfo
